DROP TABLE IF EXISTS `pre_gds_bookshelf`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `pre_gds_bookshelf` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `book_name` varchar(155) NOT NULL COMMENT '书名',
  `author` varchar(155) NOT NULL COMMENT '作者',
  `publish_man` varchar(10) NOT NULL DEFAULT 'admin' COMMENT '发布人',
  `introduce` varchar(255) NOT NULL COMMENT '介绍',
  `t_id` text NOT NULL COMMENT '关联类型ID',
  `img_url` varchar(155) NOT NULL DEFAULT '' COMMENT '图片地址',
  `file_url` varchar(155) NOT NULL COMMENT '小说文件路径',
  `status` char(55) NOT NULL COMMENT '状态值',
  `sort` tinyint(1) NOT NULL COMMENT '排序',
  `create_time` int(10) NOT NULL COMMENT '创建时间',
  `recom_time` int(10) NOT NULL COMMENT '推荐时间',
  `size` varchar(55) NOT NULL COMMENT '书的大小 ，KB',
  `is_recom` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐 ，0，1=不推荐，2=推荐',
  `download` int(10) NOT NULL DEFAULT '9' COMMENT '小说下载量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=169 DEFAULT CHARSET=utf8 COMMENT='书架表';
/*!40101 SET character_set_client = @saved_cs_client */;

